// DM.h#pragma once#include <TArray.h>#include <TArrayIterator.h>class LFileStream;class CPageStore;class CShapeStore;class CResStore;class DM {public:	DM();		~DM();		void ReadFromFile(LFileStream* file);		void WriteToFile(LFileStream* file);		// pages		int getTotalPages();		CPageStore* newPageStore();		CPageStore* getPageStore(Int32 pageNum);		// if non-existant, then return NULL		CPageStore* getPageStore(ConstStringPtr name);		TArray<CShapeStore*> getShapesInPage(Int32 pageNum);		void removePageStore(Int32 pageNum);		// shapes		int getTotalShapes();		CShapeStore* newShapeStore();		CShapeStore* getShapeStore(ConstStringPtr name);		void removeShapeStore(ConstStringPtr name);		// resources		int getTotalPicts();		int getTotalSounds();		CResStore* newResStore(ConstStringPtr type);		CResStore* getResStore(ConstStringPtr name);		TArray<CResStore*> getResList();		void removeResStore(ConstStringPtr name);	private:		#pragma warn_hidevirtual off	// bypass some warnings the TArray causes	TArray<CPageStore*> mPages;		TArray<CShapeStore*> mShapes;		TArray<CResStore*> mPicts;		TArray<CResStore*> mSounds;	#pragma warn_hidevirtual reset};const ConstStringPtr TYPE		= "\ptype";const ConstStringPtr PAGE		= "\ppage";const ConstStringPtr SHAPE		= "\pshape";const ConstStringPtr SOUND		= "\psound";const ConstStringPtr PICT		= "\ppict";const ConstStringPtr NAME		= "\pname";const ConstStringPtr PAGENUM	= "\ppagenum";