#pragma once#include <LTextEditView.h>class CEditField : public LTextEditView, public LBroadcaster {public:	enum { class_ID = FOUR_CHAR_CODE('cFLD') };	CEditField(LStream *inStream) : LTextEditView(inStream) {}	void DontBeTarget() {		BroadcastMessage(kScriptEdit,this);		LTextEditView::DontBeTarget();	}	Boolean	HandleKeyPress(		const EventRecord	&inKeyEvent) {			Boolean		keyHandled = true;	EKeyStatus	theKeyStatus = keyStatus_Input;	SInt16		theKey = (SInt16) (inKeyEvent.message & charCodeMask);	LCommander	*theTarget = GetTarget();			if (theKey == kEnterKeyID) {			LWindow *wind = LWindow::FindWindowByID(kBunnyWindowID);			if (wind) SwitchTarget(wind);			else SwitchTarget(LCommander::GetTopCommander());			return true;		}		return LTextEditView::HandleKeyPress(inKeyEvent);	}};