#include <LBevelButton.h>#include <sound.h>// the button for playing sounds in shape editorclass CSoundButton : public LBevelButton {public:	enum { class_ID = FOUR_CHAR_CODE('csnD') };	CSoundButton(LStream *inStream) : LBevelButton(inStream) {	mSoundChannel = NULL;	OSErr err = ::SndNewChannel(&mSoundChannel, sampledSynth, 0, NULL);	// err==0 means it worked		}	~CSoundButton() {		::SndDisposeChannel(mSoundChannel, true);	}		void setHandle(Handle sound) { this->sound = sound; }	void playSound() {		::SndPlay(mSoundChannel, (SndListHandle)sound, true);	}	void HotSpotResult(SInt16 inHotSpot) {		playSound();		LBevelButton::HotSpotResult(inHotSpot);	}	private:	Handle sound;	SndChannelPtr mSoundChannel;};